import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import MapView from '../components/MapView';
import LocationSearch from '../components/LocationSearch';

const ClimateMap = () => {
  const [selectedLocation, setSelectedLocation] = useState(null);
  const [climateEvents, setClimateEvents] = useState([]);
  const [weatherData, setWeatherData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [weatherLoading, setWeatherLoading] = useState(false);
  const [activeFilters, setActiveFilters] = useState(['bushfires', 'floods', 'droughts']);
  const [selectedEvent, setSelectedEvent] = useState(null);

  // Sample climate events data with proper filtering
  const sampleEvents = [
    { id: 1, type: 'bushfires', title: 'Black Summer Bushfires', date: '2019-2020', description: 'Unprecedented bushfire season affecting millions of hectares across Australia.', severity: 'extreme' },
    { id: 2, type: 'floods', title: 'Queensland Floods', date: '2022', description: 'Major flooding events across Queensland and northern NSW.', severity: 'high' },
    { id: 3, type: 'droughts', title: 'Millennium Drought', date: '1997-2009', description: 'Prolonged drought period affecting southeastern Australia.', severity: 'extreme' },
    { id: 4, type: 'bushfires', title: 'Blue Mountains Fires', date: '2013', description: 'Significant bushfire activity in the Blue Mountains region.', severity: 'medium' },
    { id: 5, type: 'floods', title: 'Brisbane River Floods', date: '2011', description: 'Major flooding of the Brisbane River affecting thousands.', severity: 'high' },
    { id: 6, type: 'droughts', title: 'Current Drought Conditions', date: '2023-2024', description: 'Ongoing drought conditions in parts of NSW and Victoria.', severity: 'medium' }
  ];

  const eventTypes = [
    { id: 'bushfires', label: 'Bushfires', icon: '🔥', color: 'bg-red-500', activeColor: 'bg-red-600' },
    { id: 'floods', label: 'Floods', icon: '🌊', color: 'bg-blue-500', activeColor: 'bg-blue-600' },
    { id: 'droughts', label: 'Droughts', icon: '🌵', color: 'bg-yellow-500', activeColor: 'bg-yellow-600' }
  ];

  const fetchWeatherData = async (location) => {
    if (!location?.lat || !location?.lon) return;

    setWeatherLoading(true);
    try {
      const response = await fetch(`http://localhost:5000/api/weather?lat=${location.lat}&lon=${location.lon}`);
      const data = await response.json();
      setWeatherData(data);
    } catch (error) {
      console.error('Error fetching weather data:', error);
      setWeatherData(null);
    } finally {
      setWeatherLoading(false);
    }
  };

  const handleLocationSelect = async (location) => {
    setLoading(true);
    setSelectedLocation(location);

    // Fetch climate events
    try {
      const response = await fetch(`http://localhost:5000/api/climate/events?location=${encodeURIComponent(location.name)}`);
      const data = await response.json();
      setClimateEvents(data.events || sampleEvents);
    } catch (error) {
      console.error('Error fetching climate data:', error);
      setClimateEvents(sampleEvents);
    } finally {
      setLoading(false);
    }

    // Fetch weather data
    fetchWeatherData(location);
  };

  const toggleFilter = (filterId) => {
    setActiveFilters(prev => {
      const newFilters = prev.includes(filterId) 
        ? prev.filter(id => id !== filterId)
        : [...prev, filterId];

      // Ensure at least one filter is always active
      return newFilters.length > 0 ? newFilters : [filterId];
    });
  };

  // Filter events based on active filters
  const filteredEvents = (climateEvents.length > 0 ? climateEvents : sampleEvents).filter(event => 
    activeFilters.includes(event.type)
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-blue-50">
      {/* Header */}
      <header className="bg-white/80 backdrop-blur-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-6 py-4">
          <Link to="/" className="text-blue-600 hover:text-blue-800 transition-colors text-sm font-medium">
            ← Back to Home
          </Link>
          <div className="mt-3">
            <h1 className="text-3xl font-bold text-gray-900">Climate Memory Map</h1>
            <p className="text-gray-600 mt-1">Historical climate events and future projections</p>
          </div>
        </div>
      </header>

      {/* Search Section */}
      <div className="max-w-7xl mx-auto px-6 py-8">
        <div className="mb-8">
          <LocationSearch onLocationSelect={handleLocationSelect} />
        </div>

        {/* Main Content */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Map Container */}
          <div className="lg:col-span-2">
            <div className="bg-white/90 backdrop-blur-sm rounded-2xl shadow-lg overflow-hidden" style={{ height: '500px' }}>
              <MapView />
            </div>
          </div>

          {/* Filters and Timeline */}
          <div className="space-y-6">
            {/* Event Type Filters */}
            <div className="bg-white/90 backdrop-blur-sm rounded-2xl shadow-lg p-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-4">Event Types</h3>
              <div className="space-y-3">
                {eventTypes.map(type => (
                  <button
                    key={type.id}
                    onClick={() => toggleFilter(type.id)}
                    className={`w-full flex items-center gap-3 p-3 rounded-xl transition-all ${
                      activeFilters.includes(type.id)
                        ? `${type.color} text-white shadow-md`
                        : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                    }`}
                  >
                    <span className="text-xl">{type.icon}</span>
                    <span className="font-medium">{type.label}</span>
                  </button>
                ))}
              </div>
            </div>

            {/* Timeline Summary */}
            {selectedLocation && (
              <div className="bg-white/90 backdrop-blur-sm rounded-2xl shadow-lg p-6">
                <h3 className="text-lg font-semibold text-gray-900 mb-4">
                  Climate Timeline: {selectedLocation.name}
                </h3>
                {loading ? (
                  <div className="space-y-3">
                    {[1,2,3].map(i => (
                      <div key={i} className="animate-pulse">
                        <div className="h-4 bg-gray-200 rounded w-3/4 mb-2"></div>
                        <div className="h-3 bg-gray-200 rounded w-1/2"></div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <div className="text-center text-gray-500 py-8">
                    <div className="text-4xl mb-2">📊</div>
                    <p>Climate events will appear here</p>
                  </div>
                )}
              </div>
            )}
          </div>
        </div>

        {/* Timeline Section */}
        {selectedLocation && !loading && (
          <div className="mt-8 bg-white/90 backdrop-blur-sm rounded-2xl shadow-lg p-6">
            <div className="flex items-center justify-between mb-6">
              <h3 className="text-xl font-semibold text-gray-900">Historical Events & Projections</h3>
              <div className="flex gap-2">
                <div className="flex items-center gap-2 text-sm text-gray-600">
                  <div className="w-3 h-3 bg-gray-400 rounded-full"></div>
                  <span>Historical</span>
                </div>
                <div className="flex items-center gap-2 text-sm text-gray-600">
                  <div className="w-3 h-3 bg-blue-500 rounded-full"></div>
                  <span>Good Scenario</span>
                </div>
                <div className="flex items-center gap-2 text-sm text-gray-600">
                  <div className="w-3 h-3 bg-red-500 rounded-full"></div>
                  <span>Bad Scenario</span>
                </div>
              </div>
            </div>

            {/* Timeline */}
            <div className="relative">
              <div className="absolute left-4 top-0 bottom-0 w-0.5 bg-gray-300"></div>

              {/* Sample Timeline Events */}
              <div className="space-y-6">
                {/* Historical Events */}
                <div className="relative flex items-start gap-4">
                  <div className="w-8 h-8 bg-gray-400 rounded-full flex items-center justify-center text-white text-sm font-bold z-10">
                    🔥
                  </div>
                  <div className="flex-1 bg-gray-50 rounded-lg p-4">
                    <div className="flex justify-between items-start mb-2">
                      <h4 className="font-semibold text-gray-900">Black Summer Bushfires</h4>
                      <span className="text-sm text-gray-500">2019-2020</span>
                    </div>
                    <p className="text-gray-700 text-sm">Unprecedented bushfire season affecting millions of hectares across Australia.</p>
                  </div>
                </div>

                {/* Future Projections */}
                <div className="relative flex items-start gap-4">
                  <div className="w-8 h-8 bg-blue-500 rounded-full flex items-center justify-center text-white text-sm font-bold z-10">
                    📈
                  </div>
                  <div className="flex-1 bg-blue-50 rounded-lg p-4">
                    <div className="flex justify-between items-start mb-2">
                      <h4 className="font-semibold text-blue-900">Good Scenario 2030-2050</h4>
                      <span className="text-sm text-blue-600">Projection</span>
                    </div>
                    <p className="text-blue-800 text-sm">With effective climate action: Reduced fire risk, improved water security.</p>
                  </div>
                </div>

                <div className="relative flex items-start gap-4">
                  <div className="w-8 h-8 bg-red-500 rounded-full flex items-center justify-center text-white text-sm font-bold z-10">
                    ⚠️
                  </div>
                  <div className="flex-1 bg-red-50 rounded-lg p-4">
                    <div className="flex justify-between items-start mb-2">
                      <h4 className="font-semibold text-red-900">Bad Scenario 2030-2050</h4>
                      <span className="text-sm text-red-600">Projection</span>
                    </div>
                    <p className="text-red-800 text-sm">Without climate action: Increased extreme weather events, water scarcity.</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ClimateMap;
